import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import './App.css';
import './styles/Theme.css'; // 引入主题样式
import Home from './pages/Home';
import CV from './pages/CV';
import Publications from './pages/Publications';
import Contact from './pages/Contact';
import Education from './components/Education';
import ThemeToggle from './components/ThemeToggle'; // 引入主题切换组件

function App() {
  // 初始化主题
  useEffect(() => {
    const savedTheme = localStorage.getItem('theme') || 'dark';
    document.body.setAttribute('data-theme', savedTheme);
  }, []);

  return (
    <Router>
      <div className="app">
        {/* 将主题切换按钮包装在一个固定位置的div中 */}
        <div className="theme-toggle-wrapper">
          <ThemeToggle />
        </div>

        <header>
          <div className="container">
            <h1>Zi Wang</h1>
            <nav>
              <ul>
                <li><Link to="/"><i className="fas fa-home"></i> Home</Link></li>
                <li><Link to="/cv"><i className="fas fa-file-alt"></i> CV</Link></li>
                <li><Link to="/publications"><i className="fas fa-flask"></i> Research Experience</Link></li>
                <li><Link to="/contact"><i className="fas fa-lightbulb"></i> Interesting Things</Link></li>
                <li><Link to="/education"><i className="fas fa-envelope"></i> Contact</Link></li>
              </ul>
            </nav>
          </div>
        </header>

        <main className="container">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/cv" element={<CV />} />
            <Route path="/publications" element={<Publications />} />
            <Route path="/contact" element={<Contact />} />
            <Route path="/education" element={<Education />} />
          </Routes>
        </main>

      </div>
    </Router>
  );
}

export default App;
