import React from 'react';
import { motion } from 'framer-motion';
import styled from 'styled-components';
import { FaMapMarkerAlt, FaPhone, FaEnvelope, FaGlobe } from 'react-icons/fa';

const HeaderContainer = styled.header`
  background-color: var(--card-bg);
  padding: 5rem 0 3rem;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: -10%;
    right: -10%;
    width: 50%;
    height: 80%;
    background: linear-gradient(135deg, rgba(26, 115, 232, 0.1), rgba(52, 168, 83, 0.05));
    border-radius: 50%;
    z-index: 0;
  }

  &::after {
    content: '';
    position: absolute;
    bottom: -20%;
    left: -10%;
    width: 70%;
    height: 60%;
    background: linear-gradient(135deg, rgba(52, 168, 83, 0.05), rgba(66, 133, 244, 0.1));
    border-radius: 50%;
    z-index: 0;
  }
`;

const HeaderContent = styled.div`
  position: relative;
  z-index: 1;
  text-align: center;
  max-width: 800px;
  margin: 0 auto;
`;

const Title = styled(motion.h1)`
  font-size: 4rem;
  font-weight: 700;
  color: var(--dark-accent);
  margin: 0;

  @media (max-width: 768px) {
    font-size: 3rem;
  }

  @media (max-width: 576px) {
    font-size: 2.5rem;
  }
`;

const Subtitle = styled(motion.p)`
  font-size: 1.2rem;
  color: var(--primary-color);
  margin: 0.8rem 0 2rem;
  font-weight: 400;
`;

const ContactBrief = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 1.5rem;
  margin-top: 2rem;

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 0.8rem;
    align-items: center;
  }
`;

const ContactItem = styled(motion.span)`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-size: 0.95rem;

  svg {
    color: var(--primary-color);
  }

  a {
    color: var(--primary-color);
    transition: all 0.3s ease;

    &:hover {
      color: var(--dark-accent);
      text-decoration: underline;
    }
  }
`;

const Header = () => {
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: { 
      opacity: 1,
      transition: { 
        staggerChildren: 0.3 
      }
    }
  };

  const itemVariants = {
    hidden: { y: -20, opacity: 0 },
    visible: { 
      y: 0, 
      opacity: 1,
      transition: { type: "spring", stiffness: 100 }
    }
  };

  const contactVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: { 
      y: 0, 
      opacity: 1,
      transition: { 
        type: "spring", 
        stiffness: 50,
        delay: 0.5,
        staggerChildren: 0.1 
      }
    }
  };

  return (
    <HeaderContainer>
      <HeaderContent as={motion.div}
        variants={containerVariants}
        initial="hidden"
        animate="visible"
      >
        <Title variants={itemVariants}>
          ZI WANG
        </Title>
        <Subtitle variants={itemVariants}>
          政治学研究者 | 数字媒体专家
        </Subtitle>

        <ContactBrief as={motion.div} 
          variants={contactVariants}
        >
          <ContactItem variants={itemVariants}>
            <FaMapMarkerAlt /> Beijing, China
          </ContactItem>
          <ContactItem variants={itemVariants}>
            <FaPhone /> (86)18230263699
          </ContactItem>
          <ContactItem variants={itemVariants}>
            <FaEnvelope /> zi.wang.prince@hotmail.com
          </ContactItem>
          <ContactItem variants={itemVariants}>
            <FaGlobe /> <a href="https://ziwang.quarto.pub/zi-wang" target="_blank" rel="noopener noreferrer">
              ziwang.quarto.pub/zi-wang
            </a>
          </ContactItem>
        </ContactBrief>
      </HeaderContent>
    </HeaderContainer>
  );
};

export default Header;
