import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const FooterContainer = styled.footer`
  padding: 30px 0;
  width: 100%;
  margin-top: 60px;
  background-color: ${props => props.theme === 'dark' ? 
    'rgba(18, 18, 18, 0.7)' : 
    'rgba(248, 249, 250, 0.7)'};
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  box-shadow: 0 -4px 20px ${props => props.theme === 'dark' ? 
    'rgba(0, 0, 0, 0.2)' : 
    'rgba(0, 0, 0, 0.1)'};
  border-top: 1px solid ${props => props.theme === 'dark' ? 
    'rgba(255, 255, 255, 0.05)' : 
    'rgba(0, 0, 0, 0.05)'};
  transition: all 0.3s ease;
`;

const FooterContent = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const FooterTop = styled.div`
  display: flex;
  justify-content: space-between;
  width: 100%;
  margin-bottom: 20px;

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 20px;
  }
`;

const FooterSection = styled.div`
  flex: 1;
  max-width: 300px;

  h3 {
    font-size: 1.2rem;
    margin-bottom: 15px;
    color: ${props => props.theme === 'dark' ? '#ffffff' : '#333333'};
    position: relative;
    display: inline-block;

    &:after {
      content: '';
      position: absolute;
      bottom: -5px;
      left: 0;
      width: 40px;
      height: 2px;
      background: ${props => props.theme === 'dark' ? 
        'linear-gradient(90deg, #4a4ad4, #90f)' : 
        'linear-gradient(90deg, #1a73e8, #34a853)'};
    }
  }

  p, a {
    color: ${props => props.theme === 'dark' ? '#c0c0c0' : '#555555'};
    font-size: 0.9rem;
    line-height: 1.6;
    margin-bottom: 10px;
  }

  a {
    display: block;
    text-decoration: none;
    transition: all 0.2s ease;

    &:hover {
      color: ${props => props.theme === 'dark' ? '#ffffff' : '#000000'};
      transform: translateX(5px);
    }
  }
`;

const SocialLinks = styled.div`
  display: flex;
  gap: 15px;
  margin-top: 10px;

  a {
    color: ${props => props.theme === 'dark' ? '#c0c0c0' : '#555555'};
    font-size: 1.2rem;
    transition: all 0.3s ease;

    &:hover {
      color: ${props => props.theme === 'dark' ? '#90f' : '#1a73e8'};
      transform: translateY(-3px);
    }
  }
`;

const FooterBottom = styled.div`
  width: 100%;
  text-align: center;
  padding-top: 20px;
  border-top: 1px solid ${props => props.theme === 'dark' ? 
    'rgba(255, 255, 255, 0.05)' : 
    'rgba(0, 0, 0, 0.05)'};

  p {
    color: ${props => props.theme === 'dark' ? '#888888' : '#777777'};
    font-size: 0.85rem;
  }

  a {
    color: ${props => props.theme === 'dark' ? '#aaaaaa' : '#555555'};
    text-decoration: none;
    transition: color 0.2s ease;

    &:hover {
      color: ${props => props.theme === 'dark' ? '#ffffff' : '#000000'};
    }
  }
`;

const Heart = styled.span`
  color: ${props => props.theme === 'dark' ? '#f06' : '#f06'};
  animation: pulse 1.5s infinite;

  @keyframes pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.6; }
  }
`;

const Footer = () => {
  const [theme, setTheme] = useState('dark');

  useEffect(() => {
    const savedTheme = localStorage.getItem('theme') || 'dark';
    setTheme(savedTheme);

    const observer = new MutationObserver((mutations) => {
      mutations.forEach((mutation) => {
        if (mutation.attributeName === 'data-theme') {
          setTheme(document.body.getAttribute('data-theme'));
        }
      });
    });

    observer.observe(document.body, { attributes: true });

    return () => observer.disconnect();
  }, []);

  const currentYear = new Date().getFullYear();

  return (
    <FooterContainer theme={theme}>
      <FooterContent>
        <FooterTop>
          <FooterSection theme={theme}>
            <h3>About</h3>
            <p>Personal website of Zi Wang, showcasing my academic research, projects, and professional activities.</p>
            <SocialLinks theme={theme}>
              <a href="https://github.com" target="_blank" rel="noopener noreferrer">
                <i className="fab fa-github"></i>
              </a>
              <a href="https://linkedin.com" target="_blank" rel="noopener noreferrer">
                <i className="fab fa-linkedin"></i>
              </a>
              <a href="https://twitter.com" target="_blank" rel="noopener noreferrer">
                <i className="fab fa-twitter"></i>
              </a>
              <a href="https://scholar.google.com" target="_blank" rel="noopener noreferrer">
                <i className="fas fa-graduation-cap"></i>
              </a>
            </SocialLinks>
          </FooterSection>

          <FooterSection theme={theme}>
            <h3>Quick Links</h3>
            <a href="/">Home</a>
            <a href="/cv">Curriculum Vitae</a>
            <a href="/clickit">Click It!</a>
          </FooterSection>

          <FooterSection theme={theme}>
            <h3>Contact</h3>
            <p><i className="fas fa-envelope"></i> email@example.com</p>
            <p><i className="fas fa-map-marker-alt"></i> Address, City, Country</p>
          </FooterSection>
        </FooterTop>

        <FooterBottom theme={theme}>
          <p>&copy; {currentYear} Zi Wang. All rights reserved. Made with <Heart theme={theme}>â™¥</Heart> using React.</p>
        </FooterBottom>
      </FooterContent>
    </FooterContainer>
  );
};

export default Footer;
