import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';
import './styles/Theme.css'; // 引入主题样式
import Home from './pages/Home';
import CV from './pages/CV';
import ClickIt from './pages/clickit';
import Navbar from './components/Navbar'; // 导入统一的Navbar组件

function App() {
  // 初始化主题
  useEffect(() => {
    const savedTheme = localStorage.getItem('theme') || 'dark';
    document.body.setAttribute('data-theme', savedTheme);
    // 确保在组件挂载后可以应用相应的样式
    document.getElementById('root').classList.add('visible');
  }, []);

  return (
    <Router>
      <div className="app">
        {/* 使用统一的Navbar组件，已包含ThemeToggle */}
        <Navbar />

        {/* 移除旧的header和nav部分 */}

        <main className="container">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/cv" element={<CV />} />
            <Route path="/clickit" element={<ClickIt />} />
          </Routes>
          <div className="container" style={{ marginTop: '100px' }}>
            <h1>Welcome to My Website</h1>
            <p>This container should have a top margin of 100px</p>
          </div>
        </main>
        <footer>
          {/* 页脚组件可能放在这里 */}
        </footer>
      </div>
    </Router>
  );
}

export default App;
