import React from 'react';
import '../styles/Pages.css';
import PageFooter from '../components/PageFooter';

const Publications = () => {
  return (
    <div className="page-container">
      <h2 className="page-heading">学术成果</h2>

      {/* 页面内容 */}
      <div className="publications-content">
        <h3>期刊论文</h3>
        <div className="publication-item">
          <p><strong>王子</strong> (2023). 数字媒体时代的政治参与. <em>政治学研究</em>, 56(3), 45-62.</p>
          <p>摘要：本文探讨了数字媒体如何改变公民政治参与的形式和程度，分析了社交媒体平台在动员公众参与政治活动中的作用。</p>
        </div>

        <div className="publication-item">
          <p><strong>Wang, Z.</strong> (2022). Digital Polarization and Political Discourse. <em>Journal of Political Communication</em>, 40(2), 198-215.</p>
          <p>摘要：This paper examines how digital platforms contribute to political polarization through algorithmic content curation and echo chamber effects.</p>
        </div>

        <h3>会议论文</h3>
        <div className="publication-item">
          <p><strong>王子</strong> (2023). 算法与公共舆论：一项实证研究. 发表于第十届中国政治学年会, 北京.</p>
        </div>

        <div className="publication-item">
          <p><strong>Wang, Z.</strong> & Johnson, A. (2022). Computational Approaches to Political Text Analysis. Presented at the International Conference on Computational Social Science, Amsterdam.</p>
        </div>

        <h3>专著</h3>
        <div className="publication-item">
          <p><strong>王子</strong> (2021). <em>数字时代的政治传播</em>. 北京：社会科学文献出版社.</p>
        </div>
      </div>

      {/* 添加共享的Footer */}
      <PageFooter />
    </div>
  );
};

export default Publications;
