import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import './App.css';
import './styles/Theme.css'; // 引入主题样式
import Home from './pages/Home';
import CV from './pages/CV';
// 修改导入路径以匹配实际文件名
import ClickIt from './pages/clickit'; // 文件名已改为clickit.js
import ThemeToggle from './components/ThemeToggle'; // 引入主题切换组件

function App() {
  // 初始化主题
  useEffect(() => {
    const savedTheme = localStorage.getItem('theme') || 'dark';
    document.body.setAttribute('data-theme', savedTheme);
  }, []);

  return (
    <Router>
      <div className="app">
        {/* 将主题切换按钮包装在一个固定位置的div中 */}
        <div className="theme-toggle-wrapper">
          <ThemeToggle />
        </div>

        <header>
          <div className="container">
            <h1>Zi Wang</h1>
            <nav>
              <ul>
                <li><Link to="/"><i className="fas fa-home"></i> Home</Link></li>
                <li><Link to="/cv"><i className="fas fa-file-alt"></i> Curriculum Vitae</Link></li>
                <li><Link to="/clickit"><i className="fas fa-flask"></i> Click It!</Link></li>
                {/* 完全移除Contact和Education链接 */}
              </ul>
            </nav>
          </div>
        </header>

        <main className="container">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/cv" element={<CV />} />
            <Route path="/clickit" element={<ClickIt />} />
            {/* 修改路由路径为/clickit，与导航链接和组件名称保持一致 */}
          </Routes>
        </main>

      </div>
    </Router>
  );
}

export default App;
