import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import ThemeToggle from './ThemeToggle';

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const closeMenu = () => {
    setMenuOpen(false);
  };

  return (
    <nav className="navbar" style={{ 
      position: 'fixed',
      top: '0',
      left: 0, 
      right: 0, 
      zIndex: 1000,
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      padding: '10px 20px',
      backgroundColor: 'rgba(18, 18, 18, 0.95)',
      backdropFilter: 'blur(8px)',
      boxShadow: '0 2px 10px rgba(0, 0, 0, 0.3)'
    }}>
      <div className="navbar-logo" style={{ fontSize: '1.5rem', fontWeight: 'bold' }}>
        <Link to="/" style={{ color: 'white', textDecoration: 'none' }}>ZI WANG</Link>
      </div>
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <div className={`navbar-links ${menuOpen ? 'active' : ''}`} style={{
          display: 'flex',
          gap: '20px'
        }}>
          <Link to="/" onClick={closeMenu} style={{ color: 'white', textDecoration: 'none' }}>
            <i className="fas fa-home"></i> Home
          </Link>
          <Link to="/cv" onClick={closeMenu} style={{ color: 'white', textDecoration: 'none' }}>
            <i className="fas fa-file-alt"></i> Curriculum Vitae
          </Link>
          <Link to="/clickit" onClick={closeMenu} style={{ color: 'white', textDecoration: 'none' }}>
            <i className="fas fa-flask"></i> Click It!
          </Link>
        </div>
        <ThemeToggle />
        <div className="navbar-toggle" onClick={toggleMenu} style={{ marginLeft: '15px', cursor: 'pointer', display: 'none' }}>
          <span className="navbar-toggle-icon"></span>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
