import React, { useEffect, useState } from 'react';
import styled from 'styled-components';

const ToggleContainer = styled.button`
  position: fixed;
  top: 20px;
  right: 20px;
  background: ${props => props.theme === 'dark' ? 'rgba(255, 255, 255, 0.2)' : 'rgba(0, 0, 0, 0.2)'};
  color: ${props => props.theme === 'dark' ? '#fff' : '#000'};
  border: none;
  border-radius: 30px;
  cursor: pointer;
  display: flex;
  font-size: 0.5rem;
  justify-content: space-between;
  overflow: hidden;
  padding: 0.5rem;
  position: relative;
  width: 80px;
  height: 40px;
  z-index: 999;
  transition: all 0.3s ease;

  &:hover {
    transform: scale(1.05);
    box-shadow: 0 0 15px ${props => props.theme === 'dark' ? 'rgba(255, 255, 255, 0.3)' : 'rgba(0, 0, 0, 0.3)'};
  }
`;

const Icons = styled.div`
  height: 100%;
  width: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;
  position: relative;
`;

const SunIcon = styled.div`
  color: #f1c40f;
  font-size: 1.5rem;
  transition: all 0.3s ease;
  opacity: ${({ theme }) => (theme === 'light' ? '1' : '0.6')};
  transform: ${({ theme }) => (theme === 'light' ? 'scale(1.2)' : 'scale(1)')};
`;

const MoonIcon = styled.div`
  color: #f1c40f;
  font-size: 1.5rem;
  transition: all 0.3s ease;
  opacity: ${({ theme }) => (theme === 'dark' ? '1' : '0.6')};
  transform: ${({ theme }) => (theme === 'dark' ? 'scale(1.2)' : 'scale(1)')};
`;

const ToggleIndicator = styled.div`
  background: #fff;
  border-radius: 50%;
  height: 34px;
  width: 34px;
  position: absolute;
  top: 3px;
  left: ${props => (props.theme === 'dark' ? '3px' : '43px')};
  transition: all 0.3s cubic-bezier(0.68, -0.55, 0.27, 1.55);
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);

  &::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 24px;
    height: 24px;
    border-radius: 50%;
    background: ${props => props.theme === 'dark' ? 'linear-gradient(40deg, #8e2de2, #4a00e0)' : 'linear-gradient(40deg, #ff4b1f, #ff9068)'};
    opacity: 0.8;
    transition: all 0.3s ease;
  }
`;

const ThemeToggle = () => {
  const [theme, setTheme] = useState('dark');

  // 初始化主题
  useEffect(() => {
    const savedTheme = localStorage.getItem('theme') || 'dark';
    setTheme(savedTheme);
    document.body.setAttribute('data-theme', savedTheme);
  }, []);

  const toggleTheme = () => {
    const newTheme = theme === 'dark' ? 'light' : 'dark';
    setTheme(newTheme);
    localStorage.setItem('theme', newTheme);
    document.body.setAttribute('data-theme', newTheme);
  };

  return (
    <ToggleContainer onClick={toggleTheme} theme={theme}>
      <Icons>
        <MoonIcon theme={theme} className="toggle-icon">
          <i className="fas fa-moon"></i>
        </MoonIcon>
        <SunIcon theme={theme} className="toggle-icon">
          <i className="fas fa-sun"></i>
        </SunIcon>
        <ToggleIndicator theme={theme} />
      </Icons>
    </ToggleContainer>
  );
};

export default ThemeToggle;
